#coding: utf-8
#title_en: Buondua
#https://buondua.com/
import downloader
from utils import Downloader, clean_title

@Downloader.register
class Downloader_buondua(Downloader):
    type = 'buondua'
    URLS = ['buondua.com']
    icon = 'base64:'
    display_name = 'Buondua'
    MAX_CORE = 3
    ACCEPT_COOKIES = [r'(.*\.)?(buondua\.com)']
    user_agent = 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/115.0.0.0 Safari/537.36'
    referer = 'https://buondua.com/'

    def read(self):
        self.urls, self.filenames, self.title = get_imgs_www(self.url,self.referer,self.user_agent)

def get_imgs_www(url,referer,user_agent):
    soup = downloader.read_soup(url,referer=referer,user_agent=user_agent)
    title = soup.find('h1').text.strip()
    idz = soup.find('div', class_='article content').attrs.get('data-id')
    title = clean_title(f'{title}({idz})')
    last = len(soup.find('div', class_='pagination-list').findAll('a')) + 1
    imgs = []
    for i in range(1,last):
        if i > 1:
            soup = downloader.read_soup(f'{url}?page={i}',referer=referer,user_agent=user_agent)
        views = soup.findAll('p')
        for p in views:
            src = p.find('img').attrs['src']
            imgs.append('https://'+src[src.find('.com/')+5:])
    filenames = {}
    num = -1
    for img in imgs:
        num += 1
        filenames[img] = str(num) + '.webp'
    return imgs, filenames, title