import downloader
from utils import Downloader, Session, clean_title


@Downloader.register
class Downloader_chochox(Downloader):
    type = 'vercomicsporno'
    URLS = ['vercomicsporno.com']
    icon = 'base64:'
    display_name = 'Vercomicsporno'
    MAX_CORE = 4
    ACCEPT_COOKIES = [r'(.*\.)?(vercomicsporno\.com)']

    def read(self):
        self.session = Session()
        self.referer = self.url
        info = get_imgs_www(self.url, self.referer, self.session)
        for img in info['imgs']:
            self.urls.append(img)
        self.title = clean_title(info['title'])

def get_imgs_www(url,referer,session):
    #soup = Soup(downloader.read_html(url, session=session))
    soup = downloader.read_soup(url, referer=referer, session=session)
    info = {}
    imgs = []
    info['title'] = soup.find('h1').text.strip()
    view = soup.find('div', class_='wp-content').findAll('noscript')
    for imgv in view:
        img = imgv.find('img').attrs['src']
        if not img or not img.startswith('http'):
            continue
        imgs.append(img)
    if '/girlD.png' in imgs[-1] or '/descargavcp.png' in imgs[-1] :
        imgs.pop()
    info['imgs'] = imgs
    return info